// underscore templates
function displayUserData(e){var t="https://api.github.com/users/"+e;$.getJSON(t,function(e){if(e.message=="Not Found"||e.name=="")$("#userData").append("<h2>No User Info Found</h2>");else{var t=myUserTemplate(e);$("#userData").append(t)}})}function displayGHPages(e){var t="https://api.github.com/users/"+e+"/repos?sort=pushed&direction=desc",n=[];$.getJSON(t,function(e){var t=e;if(t.length==0)var r="<p>No repos!</p></div>";else $.each(t,function(e){if(t[e].homepage&&t[e].homepage.indexOf("pdxiii")!=-1){var r=myRepoTemplate(t[e]);n.push(r)}});$("#myGHPages").append(n.join("\r"))})}var myUserTemplate=function(e){var t=_.template('<img class="img-round img-responsive" src=" <%= avatar_url %>&s=440" alt="My Gravatar" ><br><p>since <%= moment(created_at).format("YYYY") %> </p><p> <%= location %> </p>');return t(e)},myRepoTemplate=function(e){var t=_.template('<div class="col-md-3 ghPage"><h2><a href="<%= homepage %>" > <%= name %> </a></h2><p><%= description %> </p> <br /><p><small><%= stargazers_count %> <i class="fa fa-star"></i></small></p></div>');return t(e)};$(document).ready(function(){var e=document.body.id;if(e==="home"){var t="PDXIII";displayUserData(t);displayGHPages(t)}});